INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (116, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'Under the guidance and oversight of the USD(I) and the Chairman of the Joint Chiefs of Staff, formalize and implement an Intelligence Planning Process to support adaptive planning for peacetime, contingency, and crisis action planning in support of the COCOMs, 10 DoDD 5105.21, March 18, 2008 synchronize the planning activities of intelligence entities, and coordinate all national-level planning with the ODNI.', 'DoDD|Intelligence Planning Process|Joint Chiefs of Staff|USD(I) |ODNI', ARRAY ['DoDD 5105.21', 'DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (117, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'Integrate representatives from NSA, NGA, NRO, and the DoD Counterintelligence Field Activity into the DIOCC to facilitate tasking, coordination, and requirements resolution, and in coordination with the COCOMs, provide personnel and resources in direct support of COCOM JIOCs.', 'NSA|National Reconnaissance Office|National Security Agency|NGA|DoD Counterintelligence Field Activity|COCOM|NRO', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (118, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'In conjunction with U.S. Joint Forces Command, develop a methodology for the COCOMs to assess the efficacy of Defense Intelligence to support operational objectives and recommend changes to address shortfalls; in addition, assess and evaluate Defense Intelligence tasks across the spectrum of Operations Plans (OPLANs) to identify risk and mitigation strategies, and develop recommendations for reprioritization/realignment.', 'Defense Intelligence|Operations Plans|U.S. Joint Forces Command', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (119, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'Advocate for COCOM JIOC capabilities in programmatic, policy, and planning fora, and advocate for COCOM intelligence requirements in interagency fora.', 'COCOM JIOC|COCOM', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (120, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'In coordination with USSTRATCOM and the ODNI, formulate policies and procedures to integrate and synchronize national ISR capabilities with DoD ISR assets.', 'USSTRATCOM|United States Strategic Command|ISR|ODNI|DoD ISR', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (121, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'Serve as the DoD conduit for collection coordination with interagency partners, primarily the NIC-C and ODNI Mission Managers.', 'NIC-C|ODNI Mission Managers|National Institute of Corrections|DoD', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (122, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The DHM', 'Manage JRIP activities as the Program Manager in accordance with DoD Instruction 3305.07 (Reference (ac)).', null, ARRAY ['DoDD 1000.20', 'DoDI 3305.07']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (123, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Provide implementing guidance and direction to the Heads of the DoD Components, as appropriate, for the operational integration of the Reserve Components.', 'Reserve Components', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (124, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Facilitate the integration of Reserve Component Intelligence Elements with their COCOM, CSAs, and Military Services through all phases of the Defense Intelligence process, maximizing the use of reachback pursuant to DoD Instruction 3305.07 (Reference (ac)).', 'Defense Intelligence|COCOM|Military Services', ARRAY ['DoDD 1000.20', 'DoDI 3305.07']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (125, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Perform a range of additional functions for the Department of Defense and the IC, as articulated in Enclosure 3.', 'Department of Defense', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (126, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Maintain communication with other IC agencies pursuant to References (a) and (b), and with other applicable authorities.', null, ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (127, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Evaluate the planning, programming, budgeting, and use of resources for the collection, analysis and production, and dissemination of intelligence, as well as training in support of the requirements of the Combatant Commanders and the Secretaries of the Military Departments, and carrying out the coordinating functions stipulated at Enclosure 3.', 'Combatant Commanders|Secretaries of Military Departments', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (128, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Align intelligence operations and capabilities with the NIC-C. 7.1.4.', 'Align|National Institute of Corrections', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (129, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Participate, as appropriate, in the Secretary of Defense Biennial Review of Defense Agencies and DoD Field Activities in coordination with the Director of Administration and Management (DA&M).', 'Director of Administration and Management|Defense Biennial Review of Defense Agencies|DoD Field Activities', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (130, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Notify the USD(I) and the General Counsel of the Department of Defense within 90 days of the issuance date when the Director believes a DoD issuance would damage, limit, or seriously inhibit DIA from performing its missions.', 'DoD|Defense Intelligence Agency|General Counsel of Department of Defense|USD(I) |DIA', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (131, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Conduct all DIA activities pursuant to DoD Directive 5240.01 (Reference (ad)) and DoD Regulation 5240.1-R (Reference (ae)) and, in accordance with appropriate controls and standards of conduct, report issues or activities that raise questions of legality or propriety to the USD(I), the Inspector General of the Department of Defense, the Assistant to the Secretary of Defense for Intelligence Oversight, and, as appropriate, the General Counsel of the Department of Defense.', 'Inspector General of Department of Defense|Defense for Intelligence Oversight|Defense Intelligence Agency|DoD Regulation 5240.1-R (Reference) |General Counsel of Department of Defense|USD(I) ', ARRAY ['DoDD 5240.01', 'DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (132, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Be responsible to the Chairman of the Joint Chiefs of Staff for intelligence matters affecting the Chairman�s responsibilities under Reference (a), especially requirements associated with the joint planning process, and for matters affecting the missions and responsibilities of the Combatant Commanders; carry out combat support responsibilities pursuant to Reference (i).', 'Joint Chiefs of Staff|Combatant Commanders|Chairmans', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (133, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Use existing systems, facilities, and services of the Department of Defense and other Federal agencies, when possible, to avoid duplication and to achieve maximum efficiency and economy, as well as preserve the capability of assigned facilities and other assets to accomplish the organizational mission.', 'Department of Defense', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (134, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Heads of the DoD Components', 'Provide assistance and support to the Director, DIA, in their respective fields of responsibility and within available resources for the Director, DIA, to carry out functions as assigned herein.', 'Defense Intelligence Agency|DIA', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (135, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Heads of the DoD Components', 'Comply with taskings issued by the Director, DIA, pursuant to this Directive.', 'Defense Intelligence Agency|DIA', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (136, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Heads of the DoD Components', 'Submit intelligence collection and production requirements and programs to the Director, DIA, for record purposes and validation, as applicable.', 'Defense Intelligence Agency|DIA', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (137, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Heads of the DoD Components', 'Coordinate with the Director, DIA, on all matters concerning the mission, capabilities, functions, and operations of DIA.', 'Defense Intelligence Agency|DIA', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (138, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Heads of the DoD Components', 'Appointed to serve as the Director, DIOCC, pursuant to Reference (ab), as well as References (f) and (g), which state that the DIOCC is lead DoD organization for intelligence operations and will plan, integrate, coordinate, direct, synchronize, and manage full-spectrum Defense Intelligence operations and capabilities, to include Defense collection management and ISR, in support of the COCOMs to satisfy the priorities of the Department and the Nation.', 'Defense Intelligence|DIOCC|DoD|Defense|ISR|Nation|Department', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (139, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Heads of the DoD Components', 'Communicate directly with the Heads of the DoD Components, as necessary, to carry out assigned responsibilities and functions, including the transmission of requests for advice and assistance.', 'Heads of DoD Components', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (140, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'Communications to the Combatant Commanders normally', 'Communicate with other Government officials, representatives of the Legislative Branch, members of the public, and representatives of foreign governments or other entities, as appropriate, in carrying out assigned responsibilities and functions.', 'Government|Legislative Branch', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (141, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'Communications to the Combatant Commanders normally', 'Obtain reports, information, advice, and assistance, consistent with DoD Directives 4630.05 (Reference (ah)) and DoD Instruction 8910.01 (Reference (ai)), as necessary, to carry out assigned responsibilities and functions.', null, ARRAY ['DoDD 4630.05', 'DoDI 8910.01', 'DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (142, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'Communications to the Combatant Commanders normally', 'Issue DIA regulations, manuals, handbooks, and other DIA issuances to the DoD Components in carrying out assigned responsibilities prescribed herein.', 'Defense Intelligence Agency|DIA|DoD Components', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (143, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'Communications to the Combatant Commanders normally', 'Exercise the administrative authorities contained in Enclosure 4.', 'Enclosure 4', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (144, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'As requested by the Chairman of the Joint Chiefs of Staff, review and provide recommendations to the Chairman of the Joint Chiefs of Staff and the USD(I) concerning the planning, programming, budgeting, and use of intelligence resources for the collection and production of intelligence in support of the planning and operational requirements of the Combatant Commanders and the Secretaries of the Military Departments.', 'Joint Chiefs of Staff|Combatant Commanders|USD(I) |Secretaries of Military Departments', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (145, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Develop Joint and Initial Capabilities Documents to identify the required capabilities for DIA functional activities critical to joint forces, current gaps or redundancies, and the need to resolve specific capability gaps.', 'Defense Intelligence Agency', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (146, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'In collaboration with the Chairman of the Joint Chiefs of Staff, the Secretaries of the Military Departments, and the Combatant Commanders, and other appropriate senior officials and associated fora, evaluate the contribution of the intelligence collection disciplines in support of all-source intelligence analysis within the Department.', 'Joint Chiefs of Staff|Secretaries of Military Departments|Combatant Commanders|Department', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (147, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Facilitate Defense Intelligence support for civil agency requirements in emergency situations and assist the Assistant Secretary of Defense for Homeland Defense and Americas� Security Affairs, as necessary, pursuant to Deputy Secretary of Defense Memorandum, �Implementation Guidance Regarding the Office of the Assistant Secretary of Defense for Homeland Defense,� March 25, 2003 (Reference (aj)).', 'Americas Security Affairs|Defense|Defense Memorandum|Defense for Homeland Defense|Assistant Secretary of Defense|Homeland Defense', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (148, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Serve as the ISR Sector Lead for the DCIP in accordance with DoD Directive 3020.40 (Reference (ak)).', null, ARRAY ['DoDD 3020.40', 'DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (149, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Serve as the DoD Executive Agent (EA) for the Foreign Materiel Program except for those acquisition and exploitation activities for which the Director, NSA, and the USD(I) have responsibility in accordance with DoD Directive C-3325.01E (Reference (al)).', 'EA|NSA|DoD Directive|Foreign Materiel Program|National Security Agency|USD(I) ', ARRAY ['DoDD C-3325.01E', 'DoDD 1000.20']);
