INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (151, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Participate in the IC centers, committees, and working groups established by the DNI and in comparable activities established by the Secretary of Defense.', 'Defense|Office of the Director of National Intelligence', ARRAY ['DoDD 1000.20']);
