INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (154, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Lead a dedicated senior Signature Support Mission forum with representation from the USD(I), the Under Secretary of Defense for Acquisition, Technology, and Logistics, and the Assistant Secretary of Defense for Networks and Information Integration, as well as the Joint ENCLOSURE 3 22 DoDD 5105.21, March 18, 2008 Staff, the Military Services, and the Defense Agencies, as appropriate, to oversee the execution of the signature support mission.', 'DoDD|Military Services|Defense Agencies|Defense for Acquisition, Technology|Logistics|USD(I) |Assistant Secretary of Defense|Defense for Networks and Information Integration', ARRAY ['DoDD 5105.21', 'DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (155, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Serve as the IC Executive Agent for the National Media Exploitation Center and the Underground Facilities Analysis Center, as well as Prisoner of War/Missing in Action matters pursuant to the authorities and responsibilities prescribed herein.', 'Prisoner of War Missing in Action|National Media Exploitation Center|Underground Facilities Analysis Center', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (156, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Serve as the DoD EA for the Defense Cover Program pursuant to DoD Directive S- 5105.61 (Reference (am)).', 'Defense Cover Program|DoD Directive S-|DoD', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (157, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'In conjunction with the Commander of the U.S. Special Operations Command, jointly staff and operate the DoD Special Communications Enterprise Office to serve as the focal point for special communications activities with the Department of Defense pursuant to DoD policy regarding special communications.', 'DoD|DoD Special Communications Enterprise Office|United States Special Operations Command|U.S. Special Operations Command|Department of Defense', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (158, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Plan, coordinate, and lead National Intelligence Support Teams in support of Task Force and Combatant Commanders.', 'Task Force|National Intelligence Support Teams|Combatant Commanders', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (159, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Chair the Military Intelligence Board (MIB), an advisory committee established to support the Director, DIA, in managing the GDIP and the DIA portion of the MIP.', 'Defense Intelligence Agency|DIA|MIP|MIB', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (160, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Serve as the DoD Lead Component for Diplomatic Security Matters for all noncombatant DoD elements operating under the authority of the Chief of Mission.', 'DoD|Chief of Mission', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (161, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Maintain Visual Information records, as appropriate, on all programs under the authority, direction, and control of the Director, DIA, throughout their life cycle.', 'Defense Intelligence Agency|DIA', ARRAY ['DoDD 1000.20']);
INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (162, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'The Director, DIA,', 'Exercise the authority vested in the Secretary of Defense by 5 USC 301, 302(b), 3101 and 5107, and chapter 83 of 10 USC, as amended, on the employment, direction, and general administration of DIA civilian personnel.', 'chapter 83|Defense|Defense Intelligence Agency|DIA|USC', ARRAY ['DoDD 1000.20']);
