INSERT INTO public.responsibilities (id, "filename", "documentTitle", "organizationPersonnel", "responsibilityText", "otherOrganizationPersonnel", "documentsReferenced") VALUES (166, 'DoDD 1000.20.pdf', 'Active Duty Service Determinations for Civilian or Contractual Groups', 'In fixing such rates, the Director, DIA,', 'Carry out delegations regarding the Defense Civilian Intelligence Personnel System as prescribed in DoD Directive 1400.35.', 'Defense Civilian Intelligence Personnel System|DoD Directive 1400.35', ARRAY ['DoDD 1400.35', 'DoDD 1000.20']);
